'use client';

import { DashboardCard } from '@/components/admin/dashboard-card';
import { OrdersCountCard } from '@/components/admin/orders-count-card';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Skeleton } from '@/components/ui/skeleton';
import { initialProducts } from '@/lib/constants/products';
import { initialUsers } from '@/lib/constants/users';
import { useAppSelector } from '@/lib/store/hooks';
import {
  Activity,
  DollarSign,
  Package,
  ShoppingCart,
  TrendingUp,
  Users,
} from 'lucide-react';
import { Suspense, useMemo } from 'react';

/**
 * Dashboard skeleton loader
 */
function DashboardSkeleton() {
  return (
    <div className='space-y-8'>
      <Skeleton className='h-8 w-48' />
      <div className='grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6'>
        {[1, 2, 3, 4].map((i) => (
          <Card key={i}>
            <CardHeader className='flex flex-row items-center justify-between space-y-0 pb-2'>
              <Skeleton className='h-4 w-24' />
              <Skeleton className='h-5 w-5 rounded' />
            </CardHeader>
            <CardContent>
              <Skeleton className='h-8 w-16 mb-2' />
              <Skeleton className='h-3 w-32' />
            </CardContent>
          </Card>
        ))}
      </div>
      <div className='grid grid-cols-1 lg:grid-cols-2 gap-6'>
        <Skeleton className='h-80' />
        <Skeleton className='h-80' />
      </div>
    </div>
  );
}

/**
 * Dashboard content component
 */
function DashboardContent() {
  const { orders } = useAppSelector((state) => state.orders);
  const { cart } = useAppSelector((state) => state.cart);

  // Calculate dashboard stats
  const stats = useMemo(() => {
    const totalRevenue = orders.reduce((sum, order) => sum + order.total, 0);
    const activeOrders = orders.filter(
      (o) => o.status === 'Pending' || o.status === 'Processing'
    ).length;

    return {
      products: initialProducts.length,
      users: initialUsers.length,
      orders: orders.length,
      revenue: totalRevenue,
      activeOrders,
      cartItems: cart.reduce((sum, item) => sum + item.quantity, 0),
    };
  }, [orders, cart]);

  // Calculate trends (mock data - replace with actual historical data)
  const trends = useMemo(
    () => ({
      products: { value: 12, isPositive: true },
      users: { value: 8, isPositive: true },
      orders: { value: 15, isPositive: true },
      revenue: { value: 23, isPositive: true },
    }),
    []
  );

  return (
    <div className='space-y-8'>
      {/* Header */}
      <div>
        <h1 className='text-3xl font-bold tracking-tight dark:text-white'>
          Dashboard
        </h1>
        <p className='text-slate-600 dark:text-slate-400 mt-2'>
          Overview of your store's performance
        </p>
      </div>

      {/* Stats Grid */}
      <div className='grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 xl:grid-cols-4 gap-6'>
        <DashboardCard
          title='Total Products'
          value={stats.products}
          icon={Package}
          description='Active products'
          trend={trends.products}
        />

        <DashboardCard
          title='Total Users'
          value={stats.users}
          icon={Users}
          description='Registered customers'
          trend={trends.users}
        />

        <OrdersCountCard />

        <DashboardCard
          title='Total Revenue'
          value={`$${stats.revenue.toLocaleString()}`}
          icon={DollarSign}
          description='All time revenue'
          trend={trends.revenue}
        />
      </div>

      {/* Secondary Stats */}
      <div className='grid grid-cols-1 lg:grid-cols-3 md:grid-cols-2 gap-6'>
        <Card>
          <CardHeader className='flex flex-row items-center justify-between space-y-0 pb-2'>
            <CardTitle className='text-sm font-medium text-slate-600 dark:text-slate-400'>
              Active Orders
            </CardTitle>
            <Activity className='h-5 w-5 text-slate-400' />
          </CardHeader>
          <CardContent>
            <div className='text-2xl font-bold dark:text-white'>
              {stats.activeOrders}
            </div>
            <p className='text-xs text-slate-500 dark:text-slate-400 mt-1'>
              Pending or processing
            </p>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className='flex flex-row items-center justify-between space-y-0 pb-2'>
            <CardTitle className='text-sm font-medium text-slate-600 dark:text-slate-400'>
              Average Order Value
            </CardTitle>
            <TrendingUp className='h-5 w-5 text-slate-400' />
          </CardHeader>
          <CardContent>
            <div className='text-2xl font-bold dark:text-white'>
              $
              {stats.orders > 0
                ? (stats.revenue / stats.orders).toFixed(2)
                : '0.00'}
            </div>
            <p className='text-xs text-slate-500 dark:text-slate-400 mt-1'>
              Per order
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className='flex flex-row items-center justify-between space-y-0 pb-2'>
            <CardTitle className='text-sm font-medium text-slate-600 dark:text-slate-400'>
              Cart Items
            </CardTitle>
            <ShoppingCart className='h-5 w-5 text-slate-400' />
          </CardHeader>
          <CardContent>
            <div className='text-2xl font-bold dark:text-white'>
              {stats.cartItems}
            </div>
            <p className='text-xs text-slate-500 dark:text-slate-400 mt-1'>
              Items in carts
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Recent Activity Section - Placeholder */}
      <div className='grid grid-cols-1 lg:grid-cols-2 gap-6'>
        <Card>
          <CardHeader>
            <CardTitle>Recent Orders</CardTitle>
          </CardHeader>
          <CardContent>
            <div className='space-y-4'>
              {orders.slice(0, 5).map((order) => (
                <div
                  key={order.id}
                  className='flex items-center justify-between py-2 border-b dark:border-slate-800 last:border-0'
                >
                  <div>
                    <p className='font-medium text-sm dark:text-white'>
                      Order #{order.id.slice(0, 8)}
                    </p>
                    <p className='text-xs text-slate-500 dark:text-slate-400'>
                      {new Date(order.date).toLocaleDateString()}
                    </p>
                  </div>
                  <div className='text-right'>
                    <p className='font-semibold text-sm dark:text-white'>
                      ${order.total.toFixed(2)}
                    </p>
                    <p className='text-xs text-slate-500 dark:text-slate-400'>
                      {order.status}
                    </p>
                  </div>
                </div>
              ))}
              {orders.length === 0 && (
                <p className='text-sm text-slate-500 dark:text-slate-400 text-center py-4'>
                  No orders yet
                </p>
              )}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Low Stock Products</CardTitle>
          </CardHeader>
          <CardContent>
            <div className='space-y-4'>
              {initialProducts
                .filter((p) => p.stock < 10)
                .slice(0, 5)
                .map((product) => (
                  <div
                    key={product.id}
                    className='flex items-center justify-between py-2 border-b dark:border-slate-800 last:border-0'
                  >
                    <div className='flex-1 min-w-0'>
                      <p className='font-medium text-sm dark:text-white truncate'>
                        {product.title}
                      </p>
                      <p className='text-xs text-slate-500 dark:text-slate-400'>
                        {product.category}
                      </p>
                    </div>
                    <div className='text-right ml-4'>
                      <p
                        className={`font-semibold text-sm ${
                          product.stock === 0
                            ? 'text-red-600 dark:text-red-400'
                            : product.stock < 5
                            ? 'text-orange-600 dark:text-orange-400'
                            : 'text-yellow-600 dark:text-yellow-400'
                        }`}
                      >
                        {product.stock} left
                      </p>
                    </div>
                  </div>
                ))}
              {initialProducts.filter((p) => p.stock < 10).length === 0 && (
                <p className='text-sm text-slate-500 dark:text-slate-400 text-center py-4'>
                  All products in stock
                </p>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

/**
 * Dashboard page with Suspense boundary
 */
export default function DashboardPage() {
  return (
    <Suspense fallback={<DashboardSkeleton />}>
      <DashboardContent />
    </Suspense>
  );
}

'use client';

import { use, Suspense, useTransition, useMemo } from 'react';
import { useRouter } from 'next/navigation';
import Image from 'next/image';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import { Separator } from '@/components/ui/separator';
import { Skeleton } from '@/components/ui/skeleton';
import { useAppDispatch, useAppSelector } from '@/lib/store/hooks';
import { updateOrderStatus } from '@/lib/store/thunks/orderThunks';
import { formatPrice, formatOrderDate, formatDateTime } from '@/lib/utils/formatters';
import { 
  ArrowLeft, 
  Package, 
  User, 
  MapPin, 
  CreditCard, 
  Truck,
  Calendar,
  Loader2,
  Mail,
  Phone,
  Trash2,
  Download,
  Printer
} from 'lucide-react';
import { toast } from 'sonner';
import { Order } from '@/lib/types';

interface OrderDetailsPageProps {
  params: Promise<{ id: string }>;
}

/**
 * Get status badge variant
 */
const getStatusVariant = (status: Order['status']): 'default' | 'secondary' | 'destructive' | 'outline' => {
  const variants: Partial<Record<Order['status'], 'default' | 'secondary' | 'destructive' | 'outline'>> = {
    'Pending': 'secondary',
    'Processing': 'secondary',
    'Shipped': 'default',
    'Delivered': 'outline',
    'Cancelled': 'destructive',
  };
  return variants[status] || 'default';
};

/**
 * Order details skeleton
 */
function OrderDetailsSkeleton() {
  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div className="space-y-2">
          <Skeleton className="h-8 w-64" />
          <Skeleton className="h-4 w-48" />
        </div>
        <Skeleton className="h-10 w-32" />
      </div>
      <div className="grid lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 space-y-6">
          <Skeleton className="h-64" />
          <Skeleton className="h-96" />
        </div>
        <div className="space-y-6">
          {[1, 2, 3, 4].map((i) => (
            <Skeleton key={i} className="h-32" />
          ))}
        </div>
      </div>
    </div>
  );
}

/**
 * Order not found component
 */
function OrderNotFound() {
  const router = useRouter();
  
  return (
    <div className="flex flex-col items-center justify-center min-h-[50vh] space-y-4">
      <Package className="h-16 w-16 text-slate-300 dark:text-slate-600" />
      <h2 className="text-2xl font-bold dark:text-white">Order Not Found</h2>
      <p className="text-slate-600 dark:text-slate-400 text-center max-w-md">
        The order you're looking for doesn't exist or has been removed.
      </p>
      <Button onClick={() => router.push('/admin/orders')}>
        <ArrowLeft className="h-4 w-4 mr-2" />
        Back to Orders
      </Button>
    </div>
  );
}

/**
 * Order details content
 */
function OrderDetailsContent({ orderId }: { orderId: string }) {
  const router = useRouter();
  const dispatch = useAppDispatch();
  const [isPending, startTransition] = useTransition();

  const order = useAppSelector((state) =>
    state.orders.orders.find((o) => o.id === orderId)
  );

  // Calculate subtotal
  const subtotal = useMemo(() => 
    order?.items.reduce((sum, item) => sum + (item.price * item.quantity), 0) || 0,
    [order]
  );

  const handleStatusChange = async (newStatus: Order['status']) => {
    if (!order) return;

    try {
      await dispatch(updateOrderStatus({ 
        orderId: order.id, 
        newStatus 
      })).unwrap();
      
      toast.success(`Order status updated to ${newStatus}`);
    } catch (error) {
      toast.error('Failed to update order status');
      console.error('Status update error:', error);
    }
  };

  const handlePrint = () => {
    window.print();
    toast.success('Print dialog opened');
  };

  const handleDownload = () => {
    toast.success('Invoice download started');
    // Implement invoice download logic
  };

  const handleDelete = () => {
    toast.success('Order deleted');
    router.push('/admin/orders');
    // Implement delete logic
  };

  if (!order) {
    return <OrderNotFound />;
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="space-y-1">
          <div className="flex items-center gap-3">
            <Button
              variant="ghost"
              size="icon"
              onClick={() => router.push('/admin/orders')}
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
            <h1 className="text-2xl font-bold dark:text-white">
              Order #{order.id.slice(0, 12)}
            </h1>
          </div>
          <p className="text-sm text-slate-600 dark:text-slate-400 ml-12">
            Placed on {formatDateTime(order.date)}
          </p>
        </div>

        <div className="flex items-center gap-2">
          <Button variant="outline" size="sm" onClick={handlePrint}>
            <Printer className="h-4 w-4 mr-2" />
            Print
          </Button>
          <Button variant="outline" size="sm" onClick={handleDownload}>
            <Download className="h-4 w-4 mr-2" />
            Invoice
          </Button>
          <AlertDialog>
            <AlertDialogTrigger asChild>
              <Button variant="destructive" size="sm">
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </Button>
            </AlertDialogTrigger>
            <AlertDialogContent>
              <AlertDialogHeader>
                <AlertDialogTitle>Delete Order</AlertDialogTitle>
                <AlertDialogDescription>
                  Are you sure you want to delete this order? This action cannot be undone.
                </AlertDialogDescription>
              </AlertDialogHeader>
              <AlertDialogFooter>
                <AlertDialogCancel>Cancel</AlertDialogCancel>
                <AlertDialogAction onClick={handleDelete}>Delete</AlertDialogAction>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialog>
        </div>
      </div>

      <div className="grid lg:grid-cols-3 gap-6">
        {/* Main Content */}
        <div className="lg:col-span-2 space-y-6">
          {/* Status Card */}
          <Card>
            <CardHeader>
              <div className="flex items-start justify-between">
                <div>
                  <CardTitle>Order Status</CardTitle>
                  <CardDescription className="mt-2">
                    Update the order fulfillment status
                  </CardDescription>
                </div>
                <Badge variant={getStatusVariant(order.status)}>
                  {order.status}
                </Badge>
              </div>
            </CardHeader>
            <CardContent>
              <Select
                value={order.status}
                onValueChange={(value) => handleStatusChange(value as Order['status'])}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="Pending">Pending</SelectItem>
                  <SelectItem value="Processing">Processing</SelectItem>
                  <SelectItem value="Shipped">Shipped</SelectItem>
                  <SelectItem value="Delivered">Delivered</SelectItem>
                  <SelectItem value="Cancelled">Cancelled</SelectItem>
                </SelectContent>
              </Select>
            </CardContent>
          </Card>

          {/* Order Items */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Package className="h-5 w-5" />
                Order Items ({order.items.length})
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {order.items.map((item, index) => (
                  <div key={item.cartItemId}>
                    {index > 0 && <Separator />}
                    <div className="flex items-center gap-4 py-2">
                      <div className="w-16 h-16 relative rounded-md overflow-hidden border dark:border-slate-700">
                        <Image
                          src={item.image || '/images/placeholder.jpg'}
                          alt={item.title}
                          fill
                          className="object-cover"
                        />
                      </div>
                      <div className="flex-1">
                        <p className="font-medium dark:text-white">{item.title}</p>
                        <div className="flex items-center gap-4 text-sm text-slate-500 dark:text-slate-400 mt-1">
                          <span>Qty: {item.quantity}</span>
                          <span>{formatPrice(item.price)} each</span>
                        </div>
                      </div>
                      <p className="font-semibold dark:text-white">
                        {formatPrice(item.price * item.quantity)}
                      </p>
                    </div>
                  </div>
                ))}
              </div>

              <Separator className="my-4" />

              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span className="text-slate-600 dark:text-slate-400">Subtotal</span>
                  <span className="font-medium dark:text-white">{formatPrice(subtotal)}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-slate-600 dark:text-slate-400">Shipping</span>
                  <span className="font-medium dark:text-white">{formatPrice(0)}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-slate-600 dark:text-slate-400">Tax</span>
                  <span className="font-medium dark:text-white">{formatPrice(0)}</span>
                </div>
                <Separator />
                <div className="flex justify-between font-semibold pt-2">
                  <span className="dark:text-white">Total</span>
                  <span className="dark:text-white">{formatPrice(order.total)}</span>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2 text-lg">
                <User className="h-5 w-5" />
                Customer
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <p className="font-semibold dark:text-white">
                  {order.shippingAddress.name}
                </p>
                <p className="text-sm text-slate-500 dark:text-slate-400 mt-1">
                  Customer since {formatOrderDate(order.date)}
                </p>
              </div>
              <Separator />
              <div className="space-y-2">
                <div className="flex items-center gap-2 text-sm">
                  <Mail className="h-4 w-4 text-slate-400" />
                  <span className="text-slate-600 dark:text-slate-400">
                    customer@example.com
                  </span>
                </div>
                <div className="flex items-center gap-2 text-sm">
                  <Phone className="h-4 w-4 text-slate-400" />
                  <span className="text-slate-600 dark:text-slate-400">
                    +1 (555) 123-4567
                  </span>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2 text-lg">
                <MapPin className="h-5 w-5" />
                Shipping Address
              </CardTitle>
            </CardHeader>
            <CardContent>
              <address className="not-italic text-sm text-slate-600 dark:text-slate-400 space-y-1">
                <p className="font-medium text-slate-900 dark:text-white">
                  {order.shippingAddress.name}
                </p>
                <p>{order.shippingAddress.street}</p>
                <p>
                  {order.shippingAddress.city}, {order.shippingAddress.state}{' '}
                  {order.shippingAddress.zip}
                </p>
                <p>{order.shippingAddress.country}</p>
              </address>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2 text-lg">
                <CreditCard className="h-5 w-5" />
                Payment
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                <p className="font-medium dark:text-white">{order.paymentMethod}</p>
                <p className="text-sm text-slate-500 dark:text-slate-400">
                  Payment processed successfully
                </p>
                <Badge variant="outline" className="mt-2">Paid</Badge>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

/**
 * Order details page with Suspense
 */
export default function AdminOrderDetailsPage({ params }: OrderDetailsPageProps) {
  const { id } = use(params);

  return (
    <Suspense fallback={<OrderDetailsSkeleton />}>
      <OrderDetailsContent orderId={id} />
    </Suspense>
  );
}

'use client';

import { useEffect } from 'react';
import { useProducts } from '@/lib/hooks/useProducts';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { PlusCircle, Package, Edit, Trash2 } from 'lucide-react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';

export default function AdminProductsPage() {
  const { products, isLoading, deleteProduct } = useProducts();
  const router = useRouter();

  const handleDelete = (productId: string, productTitle: string) => {
    if (confirm(`Are you sure you want to delete "${productTitle}"?`)) {
      deleteProduct(productId);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-96">
        <div className="text-slate-600 dark:text-slate-400">Loading products...</div>
      </div>
    );
  }

  const stats = {
    total: products.length,
    categories: [...new Set(products.map(p => p.category))].length,
    brands: [...new Set(products.map(p => p.brand))].length,
    lowStock: products.filter(p => p.stock < 10).length,
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
        <div className="space-y-1">
          <div className="flex items-center gap-2">
            <Package className="h-6 w-6 text-slate-600 dark:text-slate-400" />
            <h1 className="text-3xl font-bold tracking-tight dark:text-white">
              Products
            </h1>
          </div>
          <p className="text-slate-600 dark:text-slate-400">
            Manage your product catalog and inventory
          </p>
        </div>
        <Button asChild>
          <Link href="/admin/products/new">
            <PlusCircle className="h-4 w-4 mr-2" />
            Add Product
          </Link>
        </Button>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 sm:grid-cols-4 gap-4">
        <Card>
          <CardContent className="pt-6">
            <div className="text-center">
              <p className="text-sm font-medium text-slate-600 dark:text-slate-400">
                Total Products
              </p>
              <p className="text-3xl font-bold dark:text-white mt-2">
                {stats.total}
              </p>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-6">
            <div className="text-center">
              <p className="text-sm font-medium text-slate-600 dark:text-slate-400">
                Categories
              </p>
              <p className="text-3xl font-bold dark:text-white mt-2">
                {stats.categories}
              </p>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-6">
            <div className="text-center">
              <p className="text-sm font-medium text-slate-600 dark:text-slate-400">
                Brands
              </p>
              <p className="text-3xl font-bold dark:text-white mt-2">
                {stats.brands}
              </p>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-6">
            <div className="text-center">
              <p className="text-sm font-medium text-slate-600 dark:text-slate-400">
                Low Stock
              </p>
              <p className="text-3xl font-bold text-orange-600 dark:text-orange-400 mt-2">
                {stats.lowStock}
              </p>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Products List */}
      <Card>
        <CardContent className="pt-6">
          {products.length === 0 ? (
            <div className="text-center py-12">
              <Package className="h-12 w-12 mx-auto text-slate-400 mb-4" />
              <h3 className="text-lg font-semibold dark:text-white mb-2">
                No products yet
              </h3>
              <p className="text-slate-600 dark:text-slate-400 mb-4">
                Get started by creating your first product
              </p>
              <Button asChild>
                <Link href="/admin/products/new">
                  <PlusCircle className="h-4 w-4 mr-2" />
                  Add Product
                </Link>
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {products.map((product) => (
                <div
                  key={product.id}
                  className="flex items-center gap-4 p-4 border dark:border-slate-800 rounded-lg hover:bg-slate-50 dark:hover:bg-slate-800/50 transition-colors"
                >
                  {/* Product Image */}
                  <img
                    src={product.thumbnail || product.images?.[0]}
                    alt={product.title}
                    className="h-16 w-16 object-cover rounded border dark:border-slate-700"
                    onError={(e) => {
                      e.currentTarget.src = 'https://via.placeholder.com/64?text=No+Image';
                    }}
                  />

                  {/* Product Info */}
                  <div className="flex-1 min-w-0">
                    <h3 className="font-semibold dark:text-white truncate">
                      {product.title}
                    </h3>
                    <div className="flex items-center gap-4 mt-1 text-sm text-slate-600 dark:text-slate-400">
                      <span>${product.price.toFixed(2)}</span>
                      <span>•</span>
                      <span>Stock: {product.stock}</span>
                      <span>•</span>
                      <span>{product.brand}</span>
                    </div>
                  </div>

                  {/* Actions */}
                  <div className="flex gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => router.push(`/admin/products/${product.id}/edit`)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="destructive"
                      size="sm"
                      onClick={() => handleDelete(product.id, product.title)}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { use, Suspense, useTransition } from 'react';
import { useRouter } from 'next/navigation';
import { useAppDispatch, useAppSelector } from '@/lib/store/hooks';
import { UserForm } from '@/components/admin/user-form';
import { Button } from '@/components/ui/button';
import { Skeleton } from '@/components/ui/skeleton';
import { updateUserFromAdmin } from '@/lib/store/thunks/authThunks';
import { toast } from 'sonner';
import { ArrowLeft } from 'lucide-react';
import * as z from 'zod';

const formSchema = z.object({
  name: z.string().min(2, { message: 'Name must be at least 2 characters.' }),
  email: z.string().email({ message: 'Invalid email address.' }),
  password: z
    .string()
    .min(6, { message: 'Password must be at least 6 characters.' })
    .optional()
    .or(z.literal('')),
  role: z.enum(['admin', 'customer'], { message: 'Please select a role.' }),
});

export type EditUserFormValues = z.infer<typeof formSchema>;

interface EditUserPageProps {
  params: Promise<{ id: string }>;
}

/**
 * Edit user skeleton
 */
function EditUserSkeleton() {
  return (
    <div className="space-y-6">
      <Skeleton className="h-8 w-48" />
      <div className="space-y-4">
        <Skeleton className="h-10 w-full" />
        <Skeleton className="h-10 w-full" />
        <Skeleton className="h-10 w-full" />
        <Skeleton className="h-10 w-full" />
      </div>
    </div>
  );
}

/**
 * Edit user content
 */
function EditUserContent({ userId }: { userId: string }) {
  const router = useRouter();
  const dispatch = useAppDispatch();
  const [isPending, startTransition] = useTransition();
  
  const user = useAppSelector((state) =>
    state.user.users.find((u) => u.id === userId)
  );

  const handleSubmit = async (values: EditUserFormValues) => {
    startTransition(async () => {
      try {
        const result = await dispatch(updateUserFromAdmin(userId, values));
        
        if (result.success) {
          toast.success('User updated successfully');
          router.push('/admin/users');
        } else {
          toast.error(result.message || 'Failed to update user');
        }
      } catch (error) {
        toast.error('An error occurred while updating the user');
        console.error('Update user error:', error);
      }
    });
  };

  if (!user) {
    return (
      <div className="text-center py-12">
        <p className="text-slate-600 dark:text-slate-400">User not found</p>
        <Button onClick={() => router.push('/admin/users')} className="mt-4">
          Back to Users
        </Button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="space-y-4">
        <Button
          variant="ghost"
          onClick={() => router.push('/admin/users')}
          className="hover:bg-slate-100 dark:hover:bg-slate-800"
        >
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Users
        </Button>
        <div>
          <h1 className="text-3xl font-bold tracking-tight dark:text-white">
            Edit User
          </h1>
          <p className="text-slate-600 dark:text-slate-400 mt-2">
            Update user information for {user.name}
          </p>
        </div>
      </div>

      {/* Form */}
      <UserForm user={user} onSubmit={handleSubmit} isSubmitting={isPending} />
    </div>
  );
}

/**
 * Edit user page with Suspense
 */
export default function EditUserPage({ params }: EditUserPageProps) {
  const { id } = use(params);

  return (
    <Suspense fallback={<EditUserSkeleton />}>
      <EditUserContent userId={id} />
    </Suspense>
  );
}

