// Enhanced ProductForm with image upload support

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import {
  productFormSchema,
  type ProductFormValues,
} from '@/lib/schemas/product-schema';
import { Product } from '@/lib/types';
import { zodResolver } from '@hookform/resolvers/zod';
import { Loader2, Upload, Image as ImageIcon, X } from 'lucide-react';
import { useForm } from 'react-hook-form';

interface ProductFormProps {
  product?: Product | null;
  onSubmit: (values: ProductFormValues) => void | Promise<void>;
  isSubmitting?: boolean;
}

/**
 * Product form component with image upload and proper validation
 */
export const ProductForm = ({
  product,
  onSubmit,
  isSubmitting = false,
}: ProductFormProps) => {
  const [useUrlInput, setUseUrlInput] = useState(true);
  const [imagePreviews, setImagePreviews] = useState<string[]>([]);
  const [imageFiles, setImageFiles] = useState<File[]>([]);
  const [imageUrl, setImageUrl] = useState(product?.images?.[0] || '');

  const form = useForm<ProductFormValues>({
    resolver: zodResolver(productFormSchema),
    defaultValues: {
      title: product?.title || '',
      description: product?.description || '',
      price: product?.price || 0,
      stock: product?.stock || 0,
      brand: product?.brand || '',
      category: product?.category || '',
    },
  });

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    
    if (files.length === 0) return;

    // Validate file size (5MB max)
    const validFiles = files.filter(file => {
      if (file.size > 5 * 1024 * 1024) {
        alert(`${file.name} is too large. Max size is 5MB.`);
        return false;
      }
      return true;
    });

    // Create preview URLs
    const previews = validFiles.map(file => URL.createObjectURL(file));
    setImagePreviews(prev => [...prev, ...previews]);
    setImageFiles(prev => [...prev, ...validFiles]);
  };

  const removeImage = (index: number) => {
    // Revoke object URL to free memory
    URL.revokeObjectURL(imagePreviews[index]);
    
    setImagePreviews(prev => prev.filter((_, i) => i !== index));
    setImageFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleFormSubmit = async (values: ProductFormValues) => {
    // Convert uploaded files to base64 if any
    let images: string[] = [];

    if (!useUrlInput && imageFiles.length > 0) {
      // Convert files to base64
      const imagePromises = imageFiles.map((file: File) => {
        return new Promise<string>((resolve, reject) => {
          const reader = new FileReader();
          reader.onloadend = () => resolve(reader.result as string);
          reader.onerror = reject;
          reader.readAsDataURL(file);
        });
      });

      images = await Promise.all(imagePromises);
    } else if (useUrlInput && imageUrl) {
      // Use URL input
      images = [imageUrl];
    } else {
      // Fallback placeholder
      images = ['https://via.placeholder.com/400x400?text=No+Image'];
    }

    // Add images to form values
    const finalValues = {
      ...values,
      images,
      imageUrl: images[0],
    };

    await onSubmit(finalValues);
  };

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(handleFormSubmit)} className='space-y-6'>
        {/* Title */}
        <FormField
          control={form.control}
          name='title'
          render={({ field }) => (
            <FormItem>
              <FormLabel>Product Title</FormLabel>
              <FormControl>
                <Input
                  placeholder='e.g. Quantum QLED 65" TV'
                  disabled={isSubmitting}
                  {...field}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Description */}
        <FormField
          control={form.control}
          name='description'
          render={({ field }) => (
            <FormItem>
              <FormLabel>Description</FormLabel>
              <FormControl>
                <Textarea
                  placeholder='Describe the product features and benefits...'
                  rows={4}
                  disabled={isSubmitting}
                  {...field}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Price and Stock */}
        <div className='grid grid-cols-1 md:grid-cols-2 gap-6'>
          <FormField
            control={form.control}
            name='price'
            render={({ field }) => (
              <FormItem>
                <FormLabel>Price ($)</FormLabel>
                <FormControl>
                  <Input
                    type='number'
                    step='0.01'
                    min='0'
                    placeholder='e.g. 1299.99'
                    disabled={isSubmitting}
                    {...field}
                    value={field.value === 0 ? '' : field.value}
                    onChange={(e) => {
                      const value =
                        e.target.value === '' ? 0 : parseFloat(e.target.value);
                      field.onChange(isNaN(value) ? 0 : value);
                    }}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name='stock'
            render={({ field }) => (
              <FormItem>
                <FormLabel>Stock Quantity</FormLabel>
                <FormControl>
                  <Input
                    type='number'
                    min='0'
                    placeholder='e.g. 25'
                    disabled={isSubmitting}
                    {...field}
                    value={field.value === 0 ? '' : field.value}
                    onChange={(e) => {
                      const value =
                        e.target.value === ''
                          ? 0
                          : parseInt(e.target.value, 10);
                      field.onChange(isNaN(value) ? 0 : value);
                    }}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        {/* Brand and Category */}
        <div className='grid grid-cols-1 md:grid-cols-2 gap-6'>
          <FormField
            control={form.control}
            name='brand'
            render={({ field }) => (
              <FormItem>
                <FormLabel>Brand</FormLabel>
                <FormControl>
                  <Input
                    placeholder='e.g. AuroVision'
                    disabled={isSubmitting}
                    {...field}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name='category'
            render={({ field }) => (
              <FormItem>
                <FormLabel>Category</FormLabel>
                <FormControl>
                  <Input
                    placeholder='e.g. TVs'
                    disabled={isSubmitting}
                    {...field}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        {/* Product Images Section */}
        <div className="border dark:border-slate-800 rounded-lg p-6 space-y-4">
          <FormLabel>Product Images</FormLabel>
          
          {/* Toggle between URL and Upload */}
          <div className="flex gap-2">
            <Button
              type="button"
              variant={useUrlInput ? 'default' : 'outline'}
              size="sm"
              onClick={() => setUseUrlInput(true)}
            >
              <ImageIcon className="h-4 w-4 mr-2" />
              Image URL
            </Button>
            <Button
              type="button"
              variant={!useUrlInput ? 'default' : 'outline'}
              size="sm"
              onClick={() => setUseUrlInput(false)}
            >
              <Upload className="h-4 w-4 mr-2" />
              Upload Files
            </Button>
          </div>

          {useUrlInput ? (
            /* URL Input Mode */
            <div className="space-y-2">
              <Input
                type="url"
                placeholder="https://example.com/image.jpg"
                value={imageUrl}
                onChange={(e) => setImageUrl(e.target.value)}
                disabled={isSubmitting}
              />
              {imageUrl && (
                <div className="mt-2">
                  <img 
                    src={imageUrl} 
                    alt="Preview" 
                    className="h-32 w-32 object-cover rounded-lg border dark:border-slate-700"
                    onError={(e) => {
                      e.currentTarget.src = 'https://via.placeholder.com/150?text=Invalid+URL';
                    }}
                  />
                </div>
              )}
            </div>
          ) : (
            /* File Upload Mode */
            <div className="space-y-4">
              <div className="border-2 border-dashed dark:border-slate-700 rounded-lg p-6 text-center hover:border-slate-400 dark:hover:border-slate-600 transition-colors">
                <Input
                  id="images"
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={handleImageUpload}
                  disabled={isSubmitting}
                  className="hidden"
                />
                <label 
                  htmlFor="images"
                  className="cursor-pointer flex flex-col items-center gap-2"
                >
                  <Upload className="h-10 w-10 text-slate-400" />
                  <span className="text-sm text-slate-600 dark:text-slate-400">
                    Click to upload or drag and drop
                  </span>
                  <span className="text-xs text-slate-500">
                    PNG, JPG, WEBP up to 5MB each
                  </span>
                </label>
              </div>

              {imagePreviews.length > 0 && (
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  {imagePreviews.map((preview, index) => (
                    <div key={index} className="relative group">
                      <img
                        src={preview}
                        alt={`Preview ${index + 1}`}
                        className="h-24 w-full object-cover rounded-lg border dark:border-slate-700"
                      />
                      <Button
                        type="button"
                        variant="destructive"
                        size="icon"
                        className="absolute -top-2 -right-2 h-6 w-6 opacity-0 group-hover:opacity-100 transition-opacity"
                        onClick={() => removeImage(index)}
                        disabled={isSubmitting}
                      >
                        <X className="h-4 w-4" />
                      </Button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>

        {/* Submit Button */}
        <div className='flex gap-4 pt-4'>
          <Button
            type='submit'
            disabled={isSubmitting}
            className='min-w-[120px]'
          >
            {isSubmitting ? (
              <>
                <Loader2 className='h-4 w-4 mr-2 animate-spin' />
                Saving...
              </>
            ) : product ? (
              'Save Changes'
            ) : (
              'Create Product'
            )}
          </Button>
        </div>
      </form>
    </Form>
  );
};

'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { Loader2, ShieldAlert } from 'lucide-react';
import { useAppSelector } from '@/lib/store/hooks';
import { useAppDispatch } from '@/lib/store/hooks';
import { showToast } from '@/lib/store/thunks/uiThunks';

interface AdminAuthGuardProps {
  children: React.ReactNode;
  /**
   * Optional redirect path for non-authenticated users
   */
  authRedirectTo?: string;
  /**
   * Optional redirect path for non-admin users
   */
  unauthorizedRedirectTo?: string;
  /**
   * Custom loading component
   */
  fallback?: React.ReactNode;
}

/**
 * Admin auth guard - protects routes that require admin access
 * Checks both authentication and admin role
 */
const AdminAuthGuard: React.FC<AdminAuthGuardProps> = ({
  children,
  authRedirectTo = '/auth',
  unauthorizedRedirectTo = '/',
  fallback,
}) => {
  const { user } = useAppSelector((state) => state.user);
  const router = useRouter();
  const dispatch = useAppDispatch();
  const [isChecking, setIsChecking] = useState(true);
  const [hasChecked, setHasChecked] = useState(false);

  useEffect(() => {
    let timeoutId: NodeJS.Timeout;

    const checkAdminAccess = () => {
      // If user is undefined, we're still rehydrating from persist
      if (user === undefined) {
        timeoutId = setTimeout(checkAdminAccess, 100);
        return;
      }

      // Prevent multiple redirects
      if (hasChecked) return;
      setHasChecked(true);

      // User is not authenticated
      if (!user) {
        dispatch(showToast('You must be logged in to access this page.', 'error'));
        router.replace(authRedirectTo);
        return;
      }

      // User is authenticated but not an admin
      if (user.role !== 'admin') {
        dispatch(
          showToast('You do not have permission to access this page.', 'error')
        );
        router.replace(unauthorizedRedirectTo);
        return;
      }

      // User is an admin - allow access
      setIsChecking(false);
    };

    checkAdminAccess();

    return () => {
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
    };
  }, [user, router, dispatch, hasChecked, authRedirectTo, unauthorizedRedirectTo]);

  // Show loading state while checking
  if (isChecking) {
    return fallback || (
      <div className="flex min-h-screen items-center justify-center bg-slate-50 dark:bg-slate-900">
        <div className="text-center space-y-4">
          <Loader2 className="h-16 w-16 animate-spin text-slate-400 mx-auto" />
          <p className="text-slate-600 dark:text-slate-400 font-medium">
            Verifying admin access...
          </p>
        </div>
      </div>
    );
  }

  // User is an admin - render protected content
  return <>{children}</>;
};

export default AdminAuthGuard;

