'use client';

import { ProductGridPagination } from '@/lib/hooks/usePagination';
import { useEffect, useMemo, useRef, useState } from 'react';

import { ProductGridControls } from './ProductGridControls';
import { ProductList } from './ProductList';
import { FilterSidebar } from './filter-sidebar';
import { useFilterMetadata } from '@/lib/hooks/useFilterMetadata';
import { useProductFilters } from '@/lib/hooks/useProductFilters';
import { ProductGridProps } from '@/lib/types';
import { ActiveFiltersBanner } from '../ActiveFiltersBanner';
import { LoadingOverlay } from '../shared/LoadingOverlay';
import { MobileFilterSheet } from '../MobileFilterSheet';
import { EmptyState } from '../shared/EmptyState';

export const ProductGrid = ({
  title = 'All Products',
  subtitle = 'Find the perfect tech for you',
  products,
  totalCount,
  currentPage,
  currentCategories,
  currentBrands,
  currentMinPrice,
  currentMaxPrice,
  currentSort,
  pageSize = 8,
}: ProductGridProps) => {
  const gridRef = useRef<HTMLDivElement>(null);
  const [isSheetOpen, setIsSheetOpen] = useState(false);

  const {
    isPending,
    priceTimeoutRef,
    handleCategoriesChange,
    handleBrandsChange,
    handlePriceChange,
    handleSortChange,
    handlePageChange,
    handleClearFilters,
  } = useProductFilters();

  const { activeFiltersCount, hasActiveFilters, searchQuery } =
    useFilterMetadata(
      currentCategories,
      currentBrands,
      currentMinPrice,
      currentMaxPrice
    );

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (priceTimeoutRef.current) {
        clearTimeout(priceTimeoutRef.current);
      }
    };
  }, [priceTimeoutRef]);

  const allCategories = useMemo(
    () => [
      'all',
      'smartphones',
      'laptops',
      'audio',
      'accessories',
      'wearables',
    ],
    []
  );

  const allBrands = useMemo(
    () => ['Apple', 'Samsung', 'Sony', 'Bose', 'Google', 'Anker'],
    []
  );

  const currentTitle = useMemo(
    () => (searchQuery ? `Results for "${searchQuery}"` : title),
    [searchQuery, title]
  );

  const currentSubtitle = useMemo(
    () =>
      searchQuery
        ? `${totalCount.toLocaleString()} product${
            totalCount !== 1 ? 's' : ''
          } found`
        : subtitle,
    [searchQuery, totalCount, subtitle]
  );

  const filterSidebar = useMemo(
    () => (
      <FilterSidebar
        categories={allCategories}
        brands={allBrands}
        currentCategories={currentCategories}
        currentBrands={currentBrands}
        currentMinPrice={currentMinPrice}
        currentMaxPrice={currentMaxPrice}
        onCategoriesChange={handleCategoriesChange}
        onBrandsChange={handleBrandsChange}
        onPriceChange={handlePriceChange}
      />
    ),
    [
      allCategories,
      allBrands,
      currentCategories,
      currentBrands,
      currentMinPrice,
      currentMaxPrice,
      handleCategoriesChange,
      handleBrandsChange,
      handlePriceChange,
    ]
  );

  const wrappedClearFilters = () => {
    setIsSheetOpen(false);
    handleClearFilters();
  };

  const wrappedPageChange = (page: number) => {
    handlePageChange(page, gridRef);
  };

  return (
    <div className='bg-slate-50 dark:bg-slate-900 min-h-screen'>
      <div
        className='container mx-auto px-4 py-8 sm:py-12 lg:py-16'
        ref={gridRef}
      >
        <ProductGridControls
          title={currentTitle}
          subtitle={currentSubtitle}
          currentSort={currentSort}
          onSortChange={handleSortChange}
          onFilterToggle={() => setIsSheetOpen(true)}
        />

        {hasActiveFilters && (
          <ActiveFiltersBanner
            count={activeFiltersCount}
            onClear={wrappedClearFilters}
            disabled={isPending}
          />
        )}

        <div className='lg:grid lg:grid-cols-4 lg:gap-8'>
          <aside
            className='hidden lg:block lg:col-span-1 sticky top-4 self-start'
            aria-label='Product filters'
          >
            <div className='rounded-lg border bg-card p-4 shadow-sm'>
              {filterSidebar}
            </div>
          </aside>

          <main className='lg:col-span-3'>
            <div className='relative'>
              <LoadingOverlay isLoading={isPending} />
              <ProductList products={products} />
            </div>

            {products.length === 0 && !isPending && (
              <EmptyState
                hasFilters={hasActiveFilters}
                onClearFilters={wrappedClearFilters}
              />
            )}

            {products.length > 0 && (
              <ProductGridPagination
                currentPage={currentPage}
                totalCount={totalCount}
                pageSize={pageSize}
                onPageChange={wrappedPageChange}
              />
            )}
          </main>
        </div>
      </div>

      <MobileFilterSheet
        isOpen={isSheetOpen}
        onOpenChange={setIsSheetOpen}
        activeFiltersCount={activeFiltersCount}
        hasActiveFilters={hasActiveFilters}
        onClearFilters={wrappedClearFilters}
        isPending={isPending}
      >
        {filterSidebar}
      </MobileFilterSheet>
    </div>
  );
};

'use client';

import { useState, useMemo, useCallback } from 'react';
import Image from 'next/image';
import Link from 'next/link';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Product } from '@/lib/types';
import { cn } from '@/lib/utils';

interface ProductImageCarouselProps {
  product: Product;
}

export function ProductImageCarousel({ product }: ProductImageCarouselProps) {
  const [activeImageIndex, setActiveImageIndex] = useState(0);
  const [isImageLoaded, setIsImageLoaded] = useState(false);

  // Get all available images
  const images = useMemo(() => {
    const productImages = product.images || [];
    const variantImages = product.options?.[0]?.variants
      ?.map((v) => v.image)
      .filter(Boolean) || [];
    
    const allImages = [...productImages, ...variantImages];
    return allImages.length > 0 ? allImages : ['/images/placeholder.jpg'];
  }, [product.images, product.options]);

  const currentImage = images[activeImageIndex];
  const hasMultipleImages = images.length > 1;

  const handlePrevImage = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setActiveImageIndex((prev) => (prev === 0 ? images.length - 1 : prev - 1));
    setIsImageLoaded(false);
  }, [images.length]);

  const handleNextImage = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setActiveImageIndex((prev) => (prev === images.length - 1 ? 0 : prev + 1));
    setIsImageLoaded(false);
  }, [images.length]);

  const handleDotClick = useCallback((e: React.MouseEvent, index: number) => {
    e.preventDefault();
    e.stopPropagation();
    setActiveImageIndex(index);
    setIsImageLoaded(false);
  }, []);

  // Check stock status
  const stockStatus = useMemo(() => {
    if (product.stock === 0) return { label: 'Out of Stock', variant: 'destructive' as const };
    if (product.stock < 10) return { label: `Only ${product.stock} left`, variant: 'secondary' as const };
    return null;
  }, [product.stock]);

  return (
    <div className="relative aspect-square h-48 w-full overflow-hidden bg-slate-100 dark:bg-slate-600">
      {/* Main Image */}
      <Link href={`/products/${product.id}`} className="relative block h-full w-full">
        <Image
          src={currentImage as string}
          alt={`${product.title} - Image ${activeImageIndex + 1}`}
          fill
          className={cn(
            'object-cover transition-all duration-500',
            isImageLoaded ? 'scale-100 opacity-100' : 'scale-95 opacity-0',
            'group-hover:scale-105'
          )}
          sizes="(max-width: 640px) 100vw, (max-width: 1024px) 50vw, 33vw"
          onLoad={() => setIsImageLoaded(true)}
          priority={activeImageIndex === 0}
        />
      </Link>

      {/* Stock Status Badge */}
      {stockStatus && (
        <Badge 
          variant={stockStatus.variant}
          className="absolute top-3 left-3 z-10 shadow-md"
        >
          {stockStatus.label}
        </Badge>
      )}

      {/* Navigation Arrows - Only show if multiple images */}
      {/* {hasMultipleImages && (
        <div className="absolute inset-0 flex items-center justify-between p-2 px-3.5 opacity-0 transition-opacity duration-300 group-hover:opacity-100">
          <Button
            size="icon"
            variant="secondary"
            className="h-8 w-8 rounded-full shadow-lg backdrop-blur-sm"
            onClick={handlePrevImage}
            aria-label="Previous image"
          >
            <ChevronLeft className="h-4 w-4" />
          </Button>
          <Button
            size="icon"
            variant="secondary"
            className="h-8 w-8 rounded-full shadow-lg backdrop-blur-sm"
            onClick={handleNextImage}
            aria-label="Next image"
          >
            <ChevronRight className="h-4 w-4" />
          </Button>
        </div>
      )} */}

      {/* Dot Indicators */}
      {hasMultipleImages && (
        <div className="absolute bottom-3 left-1/2 -translate-x-1/2 flex gap-1.5 z-10">
          {images.map((_, index) => (
            <button
              key={index}
              onClick={(e) => handleDotClick(e, index)}
              aria-label={`View image ${index + 1}`}
              className={cn(
                'h-1.5 rounded-full cursor-pointer transition-all duration-300',
                activeImageIndex === index
                  ? 'w-6 bg-white shadow-md'
                  : 'w-1.5 bg-white/60 hover:bg-white/80'
              )}
            />
          ))}
        </div>
      )}

      {/* Image Counter */}
      {hasMultipleImages && (
        <div className="absolute top-3 right-3 z-10">
          <Badge variant="secondary" className="text-xs backdrop-blur-sm">
            {activeImageIndex + 1} / {images.length}
          </Badge>
        </div>
      )}
    </div>
  );
}

'use client';

import { Button } from '@/components/ui/button';
import { Stars } from '@/components/ui/stars';
import { useAppDispatch, useAppSelector } from '@/lib/store/hooks';
import { addToCart, toggleWishlist } from '@/lib/store/thunks/cartThunks';
import { Product } from '@/lib/types';
import { cn } from '@/lib/utils';
import { formatPrice } from '@/lib/utils/formatters';
import { AnimatePresence, motion } from 'framer-motion';
import {
  Check,
  Heart,
  Minus,
  Plus,
  RotateCcw,
  ShoppingCart,
} from 'lucide-react';
import { useCallback, useMemo, useState, useTransition } from 'react';

interface ProductPurchasePanelProps {
  product: Product;
  selectedOptions: Record<string, string>;
  onOptionChange: (optionName: string, optionValue: string) => void;
}

export function ProductPurchasePanel({
  product,
  selectedOptions,
  onOptionChange,
}: ProductPurchasePanelProps) {
  const dispatch = useAppDispatch();
  const { itemIds: wishlistItems } = useAppSelector(
    (state: any) => state.wishlist
  );
  const cartItems = useAppSelector((state: any) => state.cart?.items);
  const [isPending, startTransition] = useTransition();
  const [quantity, setQuantity] = useState(product.stock > 0 ? 1 : 0);
  const [addedToCart, setAddedToCart] = useState(false);

  const isWished = useMemo(
    () => wishlistItems.includes(product.id),
    [wishlistItems, product.id]
  );

  // Calculate how many items of this product are already in the cart
  const quantityInCart = useMemo(() => {
    if (!cartItems || !Array.isArray(cartItems)) return 0;
    const cartItem = cartItems.find((item: any) => item.id === product.id);
    return cartItem?.quantity || 0;
  }, [cartItems, product.id]);

  // Calculate remaining stock available to add
  const availableStock = useMemo(() => {
    return Math.max(0, product.stock - quantityInCart);
  }, [product.stock, quantityInCart]);

  const isOutOfStock = useMemo(() => availableStock === 0, [availableStock]);

  // Reset quantity when available stock changes
  useMemo(() => {
    if (quantity > availableStock && availableStock > 0) {
      setQuantity(availableStock);
    } else if (availableStock === 0) {
      setQuantity(0);
    }
  }, [availableStock, quantity]);

  const handleAddToCart = useCallback(() => {
    // Prevent adding if no stock available
    if (availableStock === 0) {
      return;
    }
    
    startTransition(() => {
      dispatch(
        addToCart({
          id: product.id,
          quantity,
          title: product.title,
          price: product.price,
          options: selectedOptions,
        })
      );
      setAddedToCart(true);
      // Reset quantity to available stock or 1 after adding to cart
      setQuantity(Math.min(1, availableStock - quantity));
      setTimeout(() => setAddedToCart(false), 2000);
    });
  }, [product, quantity, selectedOptions, dispatch, availableStock]);

  return (
    <div>
      <div className='text-sm uppercase tracking-wide text-slate-500 dark:text-slate-400'>
        {product.brand}
      </div>
      <h1 className='mt-1 text-3xl font-bold tracking-tight text-slate-900 dark:text-white sm:text-4xl'>
        {product.title}
      </h1>

      <div className='mt-4 flex items-center gap-4'>
        <div className='flex items-center gap-2'>
          <Stars value={product.rating} />
          <span className='text-sm text-slate-600 dark:text-slate-400'>
            {product.rating} ({product.reviewCount} reviews)
          </span>
        </div>
      </div>

      <div className='mt-6 flex items-baseline gap-3'>
        <span className='text-3xl font-bold text-slate-900 dark:text-white sm:text-4xl'>
          {formatPrice(product.price)}
        </span>
      </div>

      <div className='mt-4'>
        {isOutOfStock ? (
          <p className='font-semibold text-red-500'>
            All items are already in your cart
          </p>
        ) : (
          <p className='text-sm text-slate-600 dark:text-slate-400'>
            Available to add: {availableStock} items
            {quantityInCart > 0 && (
              <span className='ml-1 text-slate-500'>
                ({quantityInCart} already in cart)
              </span>
            )}
          </p>
        )}
      </div>

      <p className='mt-6 leading-relaxed text-slate-600 dark:text-slate-300'>
        {product.description}
      </p>

      {product.options && product.options.length > 0 && (
        <div className='mt-6 space-y-4'>
          {product.options.map((option) => (
            <div key={option.name}>
              <h3 className='text-sm font-medium dark:text-white'>
                {option.name}
              </h3>
              <div className='mt-2 flex flex-wrap gap-2'>
                {option.variants.map((variant) => (
                  <button
                    key={variant.value}
                    type='button'
                    onClick={() => onOptionChange(option.name, variant.value)}
                    className={cn(
                      'relative flex items-center justify-center rounded-md p-0.5',
                      'focus:outline-none focus:ring-2 focus:ring-offset-2',
                      selectedOptions[option.name] === variant.value
                        ? 'ring-slate-900 dark:ring-slate-50'
                        : 'ring-transparent',
                      option.type === 'color'
                        ? 'h-8 w-8'
                        : 'h-10 px-4 text-sm font-medium border border-slate-200 dark:border-slate-700'
                    )}
                  >
                    {option.type === 'color' && (
                      <span
                        className='block h-full w-full rounded-md border border-gray-200'
                        style={{ backgroundColor: variant.value }}
                        title={variant.name}
                      />
                    )}
                    {option.type === 'size' && (
                      <span className='text-slate-900 dark:text-white'>
                        {variant.name}
                      </span>
                    )}
                  </button>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}

      <div className='mt-8 flex flex-col items-center gap-4 sm:flex-row'>
        <div className='flex items-center rounded-md border dark:border-slate-700'>
          <Button
            variant='ghost'
            size='icon'
            onClick={() => setQuantity((q) => Math.max(1, q - 1))}
            disabled={isOutOfStock}
            aria-label='Decrease quantity'
          >
            <Minus className='h-4 w-4' />
          </Button>
          <span className='w-8 text-center dark:text-white'>{quantity}</span>
          <Button
            variant='ghost'
            size='icon'
            onClick={() => setQuantity((q) => Math.min(availableStock, q + 1))}
            disabled={quantity >= availableStock}
            aria-label='Increase quantity'
          >
            <Plus className='h-4 w-4' />
          </Button>
        </div>

        <Button
          className='w-full sm:w-auto sm:flex-1'
          onClick={handleAddToCart}
          disabled={isOutOfStock || isPending}
        >
          <AnimatePresence mode='wait' initial={false}>
            <motion.span
              key={addedToCart ? 'added' : isPending ? 'adding' : 'add'}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
              className='flex items-center justify-center gap-2'
            >
              {addedToCart ? (
                <>
                  <Check className='h-5 w-5 text-green-400' /> Added!
                </>
              ) : isPending ? (
                <>
                  <RotateCcw className='h-5 w-5 animate-spin' /> Adding...
                </>
              ) : (
                <>
                  <ShoppingCart className='h-5 w-5' />{' '}
                  {isOutOfStock ? 'All in Cart' : 'Add to Cart'}
                </>
              )}
            </motion.span>
          </AnimatePresence>
        </Button>

        <Button
          variant={isWished ? 'secondary' : 'outline'}
          size='lg'
          onClick={() => dispatch(toggleWishlist(product.id))}
          aria-label={isWished ? 'Remove from wishlist' : 'Add to wishlist'}
          className='w-full sm:w-auto'
        >
          <Heart
            className={cn(
              'h-6 w-6 transition-colors',
              isWished ? 'fill-red-500 text-red-500' : ''
            )}
          />
        </Button>
      </div>
    </div>
  );
}

'use client';

import { Button } from '@/components/ui/button';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Stars } from '@/components/ui/stars';
import { useAuth } from '@/lib/hooks/useAuth';
import { useAppDispatch, useAppSelector } from '@/lib/store/hooks';
import {
  deleteReview,
  toggleHelpfulReview,
} from '@/lib/store/thunks/managementThunks';
import { Review } from '@/lib/types';
import { cn } from '@/lib/utils';
import { Pencil, ThumbsUp, Trash2 } from 'lucide-react';
import { useMemo, useState } from 'react';
import { AddReviewForm } from './add-review-form';

interface ReviewsSectionProps {
  productId: string;
}

const ReviewsSection = ({ productId }: ReviewsSectionProps) => {
  const { user } = useAuth();
  const dispatch = useAppDispatch();
  const { products } = useAppSelector((state) => state.products);
  const product = products.find((p) => p.id === productId);
  const [editingReview, setEditingReview] = useState<Review | null>(null);
  const [showConfirmDeleteDialog, setShowConfirmDeleteDialog] = useState(false);
  const [reviewIdToDelete, setReviewIdToDelete] = useState<string | null>(null);

  if (!product) return null; // Handle case where product is not found

  const reviews = product.reviews || []; // Use reviews from the product in Redux store

  const userReview = useMemo(() => {
    if (!user) return null;
    return reviews.find((review) => review.author === user.name) || null;
  }, [reviews, user]);

  const handleEditClick = (review: Review) => {
    setEditingReview(review);
  };

  const handleCancelEdit = () => {
    setEditingReview(null);
  };

  const handleToggleHelpful = (reviewId: string) => {
    dispatch(toggleHelpfulReview({ productId, reviewId }));
  };

  const handleDeleteReviewClick = (reviewId: string) => {
    setReviewIdToDelete(reviewId);
    setShowConfirmDeleteDialog(true);
  };

  const confirmDeleteReview = () => {
    if (reviewIdToDelete) {
      dispatch(deleteReview({ productId, reviewId: reviewIdToDelete }));
      setReviewIdToDelete(null);
      setShowConfirmDeleteDialog(false);
    }
  };

  const canShowAddForm = user && !userReview && !editingReview;

  return (
    <div className='mt-12 py-8 border-t dark:border-slate-800'>
      <div className='flex justify-between items-center'>
        <h2 className='text-2xl font-bold tracking-tight text-slate-900 dark:text-white'>
          Customer Reviews
        </h2>
        {user && userReview && !editingReview && (
          <div className='flex items-center gap-2'>
            <p className='text-sm text-slate-500 dark:text-slate-400'>
              You have already reviewed this product.
            </p>
            <Button
              className='flex items-center gap-2'
              variant='outline'
              size='sm'
              onClick={() => handleEditClick(userReview)}
            >
              <Pencil className='mr-2 h-4 w-4' />
              Edit Your Review
            </Button>
          </div>
        )}
      </div>

      {canShowAddForm && (
        <AddReviewForm
          productId={productId}
          reviewToEdit={null}
          onCancelEdit={handleCancelEdit}
        />
      )}

      {editingReview && (
        <AddReviewForm
          productId={productId}
          reviewToEdit={editingReview}
          onCancelEdit={handleCancelEdit}
        />
      )}

      <div className='mt-6 space-y-8'>
        {reviews.length === 0 ? (
          <p className='mt-4 text-slate-500 dark:text-slate-400'>
            No reviews yet. Be the first to share your thoughts!
          </p>
        ) : (
          reviews.map((review) => {
            const isHelpful = user?.helpfulReviews?.includes(review.id);
            const isUserReview = user && user.name === review.author;
            return (
              <div key={review.id} className='flex flex-col sm:flex-row gap-4'>
                <div className='flex-shrink-0 w-12 h-12 bg-slate-200 rounded-full flex items-center justify-center font-bold text-slate-600 dark:bg-slate-700 dark:text-slate-200'>
                  {(review.author || '?').charAt(0).toUpperCase()}
                </div>
                <div className='flex-1'>
                  <div className='flex items-center justify-between'>
                    <div className='flex items-center gap-2'>
                      <h4 className='font-semibold text-slate-900 dark:text-white'>
                        {review.author || 'Anonymous'}
                      </h4>
                      <Stars value={review.rating} />
                    </div>
                    {isUserReview && !editingReview && (
                      <div className='flex items-center gap-2'>
                        <Button
                          variant='ghost'
                          size='sm'
                          onClick={() => handleDeleteReviewClick(review.id)}
                          className='flex items-center gap-1 text-sm text-red-500 hover:text-red-600'
                        >
                          <Trash2 className='h-4 w-4' />
                          <span>Delete</span>
                        </Button>
                        <Button
                          variant='ghost'
                          size='sm'
                          onClick={() => handleEditClick(review)}
                          className='flex items-center gap-1 text-sm text-slate-500 dark:text-slate-400'
                        >
                          <Pencil className='h-4 w-4' />
                          <span>Edit</span>
                        </Button>
                      </div>
                    )}
                  </div>
                  <h5 className='font-medium mt-1 dark:text-slate-300'>
                    {review.title}
                  </h5>
                  <p className='mt-2 text-slate-600 dark:text-slate-300'>
                    {review.comment}
                  </p>
                  <div className='flex items-center mt-2 text-sm text-slate-500 dark:text-slate-400'>
                    <button
                      onClick={() => handleToggleHelpful(review.id)}
                      disabled={isUserReview as boolean | undefined} // Disable helpful button on user's own review
                      className={cn(
                        'flex items-center gap-1 transition-colors',
                        !isUserReview &&
                          'hover:text-slate-900 dark:hover:text-white',
                        isHelpful && 'text-blue-600 dark:text-blue-400',
                        isUserReview && 'cursor-not-allowed opacity-60'
                      )}
                    >
                      <ThumbsUp
                        className={cn('h-4 w-4', isHelpful && 'fill-current')}
                      />
                      <span>Helpful ({review.helpful || 0})</span>
                    </button>
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>

      <Dialog
        open={showConfirmDeleteDialog}
        onOpenChange={setShowConfirmDeleteDialog}
      >
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Confirm Deletion</DialogTitle>
            <DialogDescription>
              Are you sure you want to delete this review? This action cannot be
              undone.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button
              variant='outline'
              onClick={() => setShowConfirmDeleteDialog(false)}
            >
              Cancel
            </Button>
            <Button variant='destructive' onClick={confirmDeleteReview}>
              Confirm Delete
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export { ReviewsSection };

import { FilterX } from 'lucide-react';
import { Button } from '@/components/ui/button';

interface EmptyStateProps {
