import { useCallback } from 'react';
import { useAppDispatch } from './useAppDispatch';
import { useAppSelector } from './useAppSelector';
import { 
  createProduct as createProductThunk,
  updateProduct as updateProductThunk,
  deleteProduct as deleteProductThunk,
} from '@/lib/store/thunks/productThunks';
import { 
  addReview, 
  placeOrder, 
  toggleHelpfulReview 
} from '@/lib/store/thunks/managementThunks';
import { Order, ReviewPayload, Product } from '@/lib/types';

/**
 * Custom hook for product and order management
 * 
 * Provides access to products, orders, and related actions
 * with proper loading and error states
 */
export const useProducts = () => {
  const dispatch = useAppDispatch();
  
  // Select products state with loading and error
  const products = useAppSelector((state) => state.products.products);
  const isLoading = useAppSelector((state) => state.products.isLoading);
  const error = useAppSelector((state) => state.products.error);
  
  // Select orders state
  const orders = useAppSelector((state) => state.orders.orders);

  /**
   * Create a new product
   */
  const handleCreateProduct = useCallback(
    (productData: {
      title: string;
      description: string;
      price: number;
      stock: number;
      brand: string;
      category: string;
      images?: string[];
      imageUrl?: string;
      discount?: number;
      tags?: string[];
      options?: any[];
    }) => {
      return dispatch(createProductThunk(productData));
    },
    [dispatch]
  );

  /**
   * Update existing product
   */
  const handleUpdateProduct = useCallback(
    (productId: string, updates: Partial<Product>) => {
      return dispatch(updateProductThunk(productId, updates));
    },
    [dispatch]
  );

  /**
   * Delete product
   */
  const handleDeleteProduct = useCallback(
    (productId: string) => {
      return dispatch(deleteProductThunk(productId));
    },
    [dispatch]
  );

  /**
   * Get single product by ID
   */
  const getProduct = useCallback(
    (productId: string) => {
      return products.find(p => p.id === productId);
    },
    [products]
  );

  /**
   * Get products by category
   */
  const getProductsByCategory = useCallback(
    (category: string) => {
      return products.filter(p => p.category === category);
    },
    [products]
  );

  /**
   * Get products by brand
   */
  const getProductsByBrand = useCallback(
    (brand: string) => {
      return products.filter(p => p.brand === brand);
    },
    [products]
  );

  /**
   * Search products
   */
  const searchProducts = useCallback(
    (query: string) => {
      const lowerQuery = query.toLowerCase();
      return products.filter(
        p =>
          p.title.toLowerCase().includes(lowerQuery) ||
          p.description.toLowerCase().includes(lowerQuery) ||
          p.brand.toLowerCase().includes(lowerQuery) ||
          p.category.toLowerCase().includes(lowerQuery)
      );
    },
    [products]
  );

  /**
   * Place a new order
   */
  const handlePlaceOrder = useCallback(
    (orderDetails: Omit<Order, 'id' | 'date'>) => {
      return dispatch(placeOrder(orderDetails));
    },
    [dispatch]
  );

  /**
   * Add a review to a product
   */
  const handleAddReview = useCallback(
    (productId: string, reviewData: ReviewPayload) => {
      dispatch(addReview(productId, reviewData));
    },
    [dispatch]
  );

  /**
   * Toggle helpful count on a review
   */
  const handleUpdateReviewHelpfulCount = useCallback(
    (productId: string, reviewId: string) => {
      dispatch(toggleHelpfulReview(productId, reviewId));
    },
    [dispatch]
  );

  return {
    // State
    products,
    orders,
    isLoading,
    error,
    
    // Product CRUD
    createProduct: handleCreateProduct,
    updateProduct: handleUpdateProduct,
    deleteProduct: handleDeleteProduct,
    getProduct,
    
    // Product queries
    getProductsByCategory,
    getProductsByBrand,
    searchProducts,
    
    // Orders
    placeOrder: handlePlaceOrder,
    
    // Reviews
    addReview: handleAddReview,
    updateReviewHelpfulCount: handleUpdateReviewHelpfulCount,
  };
};

import { configureStore } from '@reduxjs/toolkit';
import {
    FLUSH,
    PAUSE,
    PERSIST,
    persistReducer,
    persistStore,
    PURGE,
    REGISTER,
    REHYDRATE,
    type Storage,
} from 'redux-persist';
import storage from 'redux-persist/lib/storage';
import rootReducer, { type RootState } from './rootReducer';

/**
 * Create no-op storage for SSR environments (Next.js, etc.)
 */
const createNoopStorage = (): Storage => ({
  getItem: (_key: string) => Promise.resolve(null),
  setItem: (_key: string, value: string) => Promise.resolve(value),
  removeItem: (_key: string) => Promise.resolve(),
});

/**
 * Get appropriate storage based on environment
 */
const getStorage = (): Storage => {
  return typeof window !== 'undefined' ? storage : createNoopStorage();
};

/**
 * Persist configuration
 * Only persist cart, wishlist, and user data
 */
const persistConfig = {
  key: 'root',
  storage: getStorage(),
  whitelist: ['cart', 'wishlist', 'user', 'orders'], // Remove 'as const' to fix type issue
  version: 1,
  timeout: 10000,
  // Optional: Add migration for version updates
  // migrate: (state: any) => {
  //   if (state?._persist?.version === 0) {
  //     // Handle migration from version 0 to 1
  //     return Promise.resolve(state);
  //   }
  //   return Promise.resolve(state);
  // },
};

/**
 * Create persisted reducer
 */
const persistedReducer = persistReducer(persistConfig, rootReducer);

/**
 * Configure Redux store with middleware and DevTools
 */
export const store = configureStore({
  reducer: persistedReducer,
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware({
      serializableCheck: {
        // Ignore redux-persist actions
        ignoredActions: [FLUSH, REHYDRATE, PAUSE, PERSIST, PURGE, REGISTER],
        // Optionally ignore Date objects in your state
        // ignoredActionPaths: ['payload.date'],
        // ignoredPaths: ['orders.items.date'],
      },
      // Enable immutability check only in development
      immutableCheck: process.env.NODE_ENV === 'development',
    }),
  // Enable Redux DevTools only in development
  devTools: process.env.NODE_ENV !== 'production' && {
    name: 'E-Commerce Store',
    trace: true, // Enable action stack traces
    traceLimit: 25,
  },
});

/**
 * Create persistor for redux-persist
 */
export const persistor = persistStore(store);

/**
 * Export types for use throughout the app
 */
export type AppDispatch = typeof store.dispatch;
export type { RootState };

/**
 * Optional: Export store state type from store directly
 * This ensures type consistency
 */
export type AppStore = typeof store;

// File: lib/store/thunks/productThunks.ts
// Simplified thunks that work with your existing productSlice

import { AppDispatch, RootState } from '../store';
import { 
  addProduct as addProductAction, 
  updateProduct as updateProductAction, 
  deleteProduct as deleteProductAction,
  setLoading,
  setError,
  clearError,
} from '../slices/productSlice';
import { showToast } from './uiThunks';
import { Product } from '@/lib/types';

interface ThunkResult {
  success: boolean;
  message?: string;
  product?: Product;
}

/**
 * Generate unique product ID
 */
const generateProductId = (): string => {
  return `product_${Date.now()}_${Math.random().toString(36).slice(2, 11)}`;
};

/**
 * Create a new product
 */
export const createProduct = (productData: {
  title: string;
  description: string;
  price: number;
  stock: number;
  brand: string;
  category: string;
  images?: string[];
  imageUrl?: string;
  discount?: number;
  tags?: string[];
  options?: any[];
}) => (
  dispatch: AppDispatch,
  getState: () => RootState
): ThunkResult => {
  try {
    dispatch(setLoading(true));
    dispatch(clearError());

    // Prepare images array
    const images = productData.images || 
                   (productData.imageUrl ? [productData.imageUrl] : 
                   ['https://via.placeholder.com/400x400?text=No+Image']);

    // Create new product
    const newProduct: Product = {
      id: generateProductId(),
      title: productData.title,
      description: productData.description,
      price: productData.price,
      stock: productData.stock,
      brand: productData.brand,
      category: productData.category,
      images,
      thumbnail: images[0],
      rating: 0,
      reviewCount: 0,
      discount: productData.discount,
      tags: productData.tags || [],
      options: productData.options || [],
      reviews: [],
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    };

    // Add to Redux state (which handles localStorage)
    dispatch(addProductAction(newProduct));
    dispatch(setLoading(false));
    dispatch(showToast('Product created successfully!', 'success'));

    return { success: true, product: newProduct };
  } catch (error) {
    console.error('Error creating product:', error);
    dispatch(setError('Failed to create product'));
    dispatch(showToast('Failed to create product', 'error'));
    return { success: false, message: 'Failed to create product' };
  }
};

/**
 * Update existing product
 */
export const updateProduct = (
  productId: string,
  updates: Partial<Omit<Product, 'id' | 'createdAt'>>
) => (
  dispatch: AppDispatch,
  getState: () => RootState
): ThunkResult => {
  try {
    dispatch(setLoading(true));
    dispatch(clearError());

    const { products } = getState().products;
    const product = products.find(p => p.id === productId);

    if (!product) {
      dispatch(setError('Product not found'));
      dispatch(showToast('Product not found', 'error'));
      return { success: false, message: 'Product not found' };
    }

    // Update product (slice handles localStorage)
    dispatch(updateProductAction({ id: productId, changes: updates }));
    dispatch(setLoading(false));
    dispatch(showToast('Product updated successfully!', 'success'));

    return { success: true, product: { ...product, ...updates } };
  } catch (error) {
    console.error('Error updating product:', error);
    dispatch(setError('Failed to update product'));
    dispatch(showToast('Failed to update product', 'error'));
    return { success: false, message: 'Failed to update product' };
  }
};

/**
 * Delete product
 */
export const deleteProduct = (productId: string) => (
  dispatch: AppDispatch,
  getState: () => RootState
): ThunkResult => {
  try {
    dispatch(setLoading(true));
    dispatch(clearError());

    const { products } = getState().products;
    const productExists = products.some(p => p.id === productId);

    if (!productExists) {
      dispatch(setError('Product not found'));
      dispatch(showToast('Product not found', 'error'));
      return { success: false, message: 'Product not found' };
    }

    // Remove from Redux state (slice handles localStorage)
    dispatch(deleteProductAction(productId));
    dispatch(setLoading(false));
    dispatch(showToast('Product deleted successfully!', 'success'));

    return { success: true };
  } catch (error) {
    console.error('Error deleting product:', error);
    dispatch(setError('Failed to delete product'));
    dispatch(showToast('Failed to delete product', 'error'));
    return { success: false, message: 'Failed to delete product' };
  }
};
